    let weightedSum = -0.04 * CreditScore + 0.05 * Age - 0.01 * Tenure - 0.61 * IsActiveMember - 0.09 * NumOfProducts - 0.13 * HasCrCard + 0.06 * EstimatedSalary;

    if (Geography === 'Germany') {
        weightedSum += 0.57;
    } else if (Geography === 'Spain') {
        weightedSum -= 0.05;
    }

    if (Gender === 'Female') {
        weightedSum += 0.41;
    } else {
        weightedSum -= 0.02;
